import React,{ useEffect, useState } from "react";
import validation from "./validation";
import {useRef} from 'react';

const SignupForm = ({submitForm}) => {

    var formUsername = "null";
    var formEmail = "null";
    var formPassword = "null";

    const inputRef = useRef(null);



    const [values, setValues] = useState({
        fullname: "",
        email: "",
        password: "",
    });

    const [errors,setErrors] = useState({});
    const [dataIsCorrect,setDataIsCorrect] = useState(false);

    const handleChange = (event) => {
        setValues({
            ...values,
            [event.target.name] : event.target.value,
        });
        if (event.target.name == "fullname")
        {
        console.log('fullname is:', event.target.value);
        formUsername = event.target.value;
        console.log('username is',formUsername)
        }
        else if (event.target.name == "email")
        {
        console.log('email is:', event.target.value);
        formEmail = event.target.value;
        console.log('username is',formEmail)
        }
        else 
        {
        console.log('password is:', event.target.value);
        formPassword = event.target.value;
        console.log('username is',formPassword)
        }
    };

    const Demo=()=>
    {
        console.log('username for this user is',inputRef.current.value)
    }

    const handleFormSubmit = (event) => {
        
        event.preventDefault();
        setErrors(validation(values));
        setDataIsCorrect(true);
    };


    const sendData=()=>
{

        // Send data to the backend via POST
        fetch('http://localhost:5000/', {  // Enter your IP address here
    
          method: 'POST', 
          mode: 'cors', 
          body: JSON.stringify() 
        })
}


    useEffect(()=>{
        if(Object.keys(errors).length === 0 && dataIsCorrect){
         submitForm(true);
        }
    }, [errors]);

    return (
     <div className="container">
        <div className="app-wrapper">
        <div>
        <h2 className="title sign up">Create Account</h2>
        </div>
        <h3 className="title log in">Log In</h3>

    <form className="form-wrapper">

    

        <div className="fullname">
            <label className="label">Full Name</label>
            <input 
                id = "fullname"
                className="input" 
                type="text"
                name="fullname"
                value={values.fullname} 
                onChange={handleChange}
               
                 />
                {errors.fullname && <p className="error">{errors.fullname}</p>}

            </div>
            <div className="email">
            <label className="label">Email</label>
            <input 
                id = "email"
                className="input" 
                type="email"
                name="email"
                value={values.email} 
                onChange={handleChange}
                
                />
               {errors.email && <p className="error">{errors.email}</p>}

            </div>
            <div className="password">
            <label className="label">Password</label>
            <input 
                id = "password"
                className="input" 
                type="password"
                name="password"
                value={values.password} 
                onChange={handleChange}
                
                />
               {errors.password && <p className="error">{errors.password}</p>}

            </div>
            <div>
                <button className="submit" onClick={handleFormSubmit}>
                Sign Up
                </button>

                <button  onClick={Demo}>
                test
                </button>
             </div> 
  
        
                </form>
                </div>
     </div>
    
     
    );

};

export default SignupForm;
